    @Override
    public void download(String filename) {
	if (linkValido) {
	    HttpClient httpclient = HttpClients.createDefault();
	    try {
		this.download(httpclient, toDownload, filename);
	    } catch (UnsupportedOperationException e) {
		e.printStackTrace();
	    } catch (ClientProtocolException e) {
		e.printStackTrace();
	    } catch (IOException e) {
		e.printStackTrace();
	    }
	}
    }
